function GM:GiveDefaultWeapons(player)

	-- Holy fuck at this.
	local map = string.lower(game.GetMap())
	
	if IsValid(player) and player:IsPlayer() then
	
		player:StripWeapons()
		player:StripAmmo()
		
		local mapdata = weapondata[map]
		if mapdata and mapdata['weapons'] then
			for k,v in pairs(mapdata['weapons']) do
			
				DbgPrint("Giving Loadout: "..tostring(v))
				local ent = ents.Create(v)
				ent:SetPos(player:GetPos())
				ent:Spawn()
				ent.ForPlayer = player
				
			end
			local ammo = mapdata['ammo']
			if ammo then
				for k,v in pairs(ammo) do
					player:SetAmmo(v,k)
				end
			end
			player:SetArmor(mapdata['armor'])
		else
			DbgPrint("Default weapon data missing for map: "..map)
		end
		
	else
		DbgPrint("Invalid player for loadout")
	end
	
end

		
